    'IFF46', 'Dr. Henry Tsang, Veronica Madonna, Dr. Trevor Butler, Dr. Douglas MacLeod, Jessica Williams', 'Athabasca University RAIC Centre for Architecture', 'Region 3', true,
    NOW()
);

